	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(MawsFilterPeer::DATABASE_NAME);

		if ($this->isColumnModified(MawsFilterPeer::ID)) $criteria->add(MawsFilterPeer::ID, $this->id);
		if ($this->isColumnModified(MawsFilterPeer::NAME)) $criteria->add(MawsFilterPeer::NAME, $this->name);
		if ($this->isColumnModified(MawsFilterPeer::RESOURCE)) $criteria->add(MawsFilterPeer::RESOURCE, $this->resource);
		if ($this->isColumnModified(MawsFilterPeer::RESOURCE_TYPE)) $criteria->add(MawsFilterPeer::RESOURCE_TYPE, $this->resource_type);
		if ($this->isColumnModified(MawsFilterPeer::RESOURCE_METHOD)) $criteria->add(MawsFilterPeer::RESOURCE_METHOD, $this->resource_method);
		if ($this->isColumnModified(MawsFilterPeer::RESOURCE_LOGIN)) $criteria->add(MawsFilterPeer::RESOURCE_LOGIN, $this->resource_login);
		if ($this->isColumnModified(MawsFilterPeer::RESOURCE_PASS)) $criteria->add(MawsFilterPeer::RESOURCE_PASS, $this->resource_pass);
		if ($this->isColumnModified(MawsFilterPeer::RESOURCE_PARAMS)) $criteria->add(MawsFilterPeer::RESOURCE_PARAMS, $this->resource_params);
		if ($this->isColumnModified(MawsFilterPeer::FILTER)) $criteria->add(MawsFilterPeer::FILTER, $this->filter);
		if ($this->isColumnModified(MawsFilterPeer::FILTER_TYPE)) $criteria->add(MawsFilterPeer::FILTER_TYPE, $this->filter_type);
		if ($this->isColumnModified(MawsFilterPeer::ACTION)) $criteria->add(MawsFilterPeer::ACTION, $this->action);
		if ($this->isColumnModified(MawsFilterPeer::ACTION_TYPE)) $criteria->add(MawsFilterPeer::ACTION_TYPE, $this->action_type);
		if ($this->isColumnModified(MawsFilterPeer::ACTION_PARAM1)) $criteria->add(MawsFilterPeer::ACTION_PARAM1, $this->action_param1);
		if ($this->isColumnModified(MawsFilterPeer::ACTION_PARAM2)) $criteria->add(MawsFilterPeer::ACTION_PARAM2, $this->action_param2);
		if ($this->isColumnModified(MawsFilterPeer::ACTION_PARAM3)) $criteria->add(MawsFilterPeer::ACTION_PARAM3, $this->action_param3);
		if ($this->isColumnModified(MawsFilterPeer::CREATED_AT)) $criteria->add(MawsFilterPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(MawsFilterPeer::UPDATED_AT)) $criteria->add(MawsFilterPeer::UPDATED_AT, $this->updated_at);

		return $criteria;
	}

