	{

		$copyObj->setName($this->name);

		$copyObj->setResource($this->resource);

		$copyObj->setResourceType($this->resource_type);

		$copyObj->setResourceMethod($this->resource_method);

		$copyObj->setResourceLogin($this->resource_login);

		$copyObj->setResourcePass($this->resource_pass);

		$copyObj->setResourceParams($this->resource_params);

		$copyObj->setFilter($this->filter);

		$copyObj->setFilterType($this->filter_type);

		$copyObj->setAction($this->action);

		$copyObj->setActionType($this->action_type);

		$copyObj->setActionParam1($this->action_param1);

		$copyObj->setActionParam2($this->action_param2);

		$copyObj->setActionParam3($this->action_param3);

		$copyObj->setCreatedAt($this->created_at);

		$copyObj->setUpdatedAt($this->updated_at);


		if ($deepCopy) {
			// important: temporarily setNew(false) because this affects the behavior of
			// the getter/setter methods for fkey referrer objects.
			$copyObj->setNew(false);

			foreach ($this->getMawsFilterResults() as $relObj) {
				if ($relObj !== $this) {  // ensure that we don't try to copy a reference to ourselves
					$copyObj->addMawsFilterResult($relObj->copy($deepCopy));
				}
			}

		} // if ($deepCopy)


		$copyObj->setNew(true);

		$copyObj->setId(NULL); // this is a auto-increment column, so set to default value

	}

