	 * @throws     PropelException
	 */
	public function getMawsFilterResults($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(MawsFilterPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collMawsFilterResults === null) {
			if ($this->isNew()) {
			   $this->collMawsFilterResults = array();
			} else {

				$criteria->add(MawsFilterResultPeer::FILTER_ID, $this->id);

				MawsFilterResultPeer::addSelectColumns($criteria);
				$this->collMawsFilterResults = MawsFilterResultPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(MawsFilterResultPeer::FILTER_ID, $this->id);

				MawsFilterResultPeer::addSelectColumns($criteria);
				if (!isset($this->lastMawsFilterResultCriteria) || !$this->lastMawsFilterResultCriteria->equals($criteria)) {
					$this->collMawsFilterResults = MawsFilterResultPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastMawsFilterResultCriteria = $criteria;
		return $this->collMawsFilterResults;
	}

