	public function addMawsFilterResult(MawsFilterResult $l)
	{
		if ($this->collMawsFilterResults === null) {
			$this->initMawsFilterResults();
		}
		if (!in_array($l, $this->collMawsFilterResults, true)) { // only add it if the **same** object is not already associated
			array_push($this->collMawsFilterResults, $l);
			$l->setMawsFilter($this);
		}
	}


